                "example": "{\"id\":10000,\"self\":\"https://your-domain.atlassian.net/rest/api/3/permissionscheme/10000\",\"name\":\"Example permission scheme\",\"description\":\"description\",\"permissions\":[{\"id\":10000,\"self\":\"https://your-domain.atlassian.net/rest/api/3/permissionscheme/permission/10000\",\"holder\":{\"type\":\"group\",\"parameter\":\"jira-core-users\",\"value\":\"ca85fac0-d974-40ca-a615-7af99c48d24f\",\"expand\":\"group\"},\"permission\":\"ADMINISTER_PROJECTS\"}]}"
              }
