                "example": "{\"statuses\":[{\"id\":\"10001\",\"statusReference\":\"10001\",\"name\":\"To Do\",\"statusCategory\":\"TODO\",\"scope\":{\"type\":\"GLOBAL\"},\"description\":\"\",\"usages\":[]},{\"id\":\"10002\",\"statusReference\":\"10002\",\"name\":\"In Progress\",\"statusCategory\":\"IN_PROGRESS\",\"scope\":{\"type\":\"GLOBAL\"},\"description\":\"\",\"usages\":[]},{\"id\":\"10003\",\"statusReference\":\"10003\",\"name\":\"Done\",\"statusCategory\":\"DONE\",\"scope\":{\"type\":\"GLOBAL\"},\"description\":\"\",\"usages\":[]}],\"workflows\":[{\"scope\":{\"type\":\"GLOBAL\"},\"version\":{\"versionNumber\":0,\"id\":\"f010ac1b-3dd3-43a3-aa66-0ee8a447f76e\"},\"id\":\"b9ff2384-d3b6-4d4e-9509-3ee19f607168\",\"name\":\"Workflow 1\",\"description\":\"\",\"isEditable\":true,\"startPointLayout\":{\"x\":-100.00030899047852,\"y\":-153.00020599365234},\"usages\":[],\"statuses\":[{\"statusReference\":\"10001\",\"layout\":{\"x\":114.99993896484375,\"y\":-16.0},\"properties\":{},\"deprecated\":false},{\"statusReference\":\"10002\",\"layout\":{\"x\":317.0000915527344,\"y\":-16.0},\"properties\":{},\"deprecated\":false},{\"statusReference\":\"10003\",\"layout\":{\"x\":508.000244140625,\"y\":-16.0},\"properties\":{},\"deprecated\":false}],\"transitions\":[{\"id\":\"31\",\"type\":\"GLOBAL\",\"from\":[],\"to\":{\"statusReference\":\"10003\"},\"name\":\"Done\",\"description\":\"\",\"actions\":[],\"validators\":[],\"triggers\":[],\"properties\":{}},{\"id\":\"11\",\"type\":\"GLOBAL\",\"from\":[],\"to\":{\"statusReference\":\"10001\"},\"name\":\"To Do\",\"description\":\"\",\"actions\":[],\"validators\":[],\"triggers\":[],\"properties\":{}},{\"id\":\"21\",\"type\":\"GLOBAL\",\"from\":[],\"to\":{\"statusReference\":\"10002\"},\"name\":\"In Progress\",\"description\":\"\",\"actions\":[],\"validators\":[],\"triggers\":[],\"properties\":{}},{\"id\":\"1\",\"type\":\"INITIAL\",\"from\":[],\"to\":{\"statusReference\":\"10001\"},\"name\":\"Create\",\"description\":\"\",\"actions\":[],\"validators\":[],\"triggers\":[],\"properties\":{}}]}]}"
              }
            }
          },
